(function($) {
    "use strict";

    // Toggle the sidebar
    $("#sidebarToggle, #sidebarToggleTop").on("click", function(e) {
        $("body").toggleClass("sidebar-toggled");
        $(".sidebar").toggleClass("toggled");

        // If sidebar is toggled, collapse any open menu
        if ($(".sidebar").hasClass("toggled")) {
            $(".sidebar .collapse").collapse("hide");
        }
    });

    // Resize event handler
    $(window).resize(function() {
        // Collapse sidebar when window width is less than 768px
        if ($(window).width() < 768) {
            $(".sidebar .collapse").collapse("hide");
        }

        // If the window width is less than 480px and the sidebar is not toggled, toggle it
        if ($(window).width() < 480 && !$(".sidebar").hasClass("toggled")) {
            $("body").addClass("sidebar-toggled");
            $(".sidebar").addClass("toggled");
            $(".sidebar .collapse").collapse("hide");
        }
    });

    // Prevent sidebar from scrolling on large screens when using the mouse wheel
    $("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function(e) {
        if ($(window).width() > 768) {
            var delta = (e.originalEvent.wheelDelta || -e.originalEvent.detail);
            this.scrollTop += 30 * (delta < 0 ? 1 : -1);
            e.preventDefault();
        }
    });

    // Show/hide scroll-to-top button based on the scroll position
    $(document).on("scroll", function() {
        if ($(this).scrollTop() > 100) {
            $(".scroll-to-top").fadeIn();
        } else {
            $(".scroll-to-top").fadeOut();
        }
    });

    // Smooth scrolling to the top when clicking the scroll-to-top button
    $(document).on("click", "a.scroll-to-top", function(e) {
        var $anchor = $(this);
        $("html, body").stop().animate({
            scrollTop: $($anchor.attr("href")).offset().top
        }, 1000, "easeInOutExpo");
        e.preventDefault();
    });

})(jQuery);



$(document).ready(function() {
    const checkbox = $('#darkModeToggle'); // Ensure you are selecting the correct element
    
    // Check if user previously selected dark mode
    if (localStorage.getItem('theme') === 'dark') {
        $('body').addClass('dark-theme');
        checkbox.prop('checked', true); // Set the checkbox to checked if dark mode is active
    }

    // On toggle button click (checkbox change)
    checkbox.on('change', function() {
        $('body').toggleClass('dark-theme'); // Toggle dark mode

        // Save the preference in localStorage
        if ($('body').hasClass('dark-theme')) {
            localStorage.setItem('theme', 'dark');
        } else {
            localStorage.setItem('theme', 'light');
        }
    });
});